document.addEventListener("DOMContentLoaded",function(){const stats=document.getElementById("stats");if(stats){const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){const statNumbers=document.querySelectorAll(".stat-number"),availabilityElement=document.querySelector(".availability-animation"),animateWithDelay=(element,delay)=>{setTimeout(()=>{if(element.classList.contains("stat-number")){const target=parseInt(element.getAttribute("data-target"));let count=0;const increment=target/(2e3/16),updateCount=()=>{count+=increment;if(count<target){element.textContent=target===100?Math.ceil(count)+"%":"+"+Math.ceil(count);requestAnimationFrame(updateCount)}else{element.textContent=target===100?target+"%":"+"+target}};updateCount()}else if(element.classList.contains("availability-animation")){element.style.opacity="0";element.style.transform="translateX(-100px) translateY(-100px)";let opacity=0,positionX=-100,positionY=-100;const animate=()=>{opacity+=.05;if(opacity>1)opacity=1;positionX+=5;if(positionX>0)positionX=0;positionY+=5;if(positionY>0)positionY=0;element.style.opacity=opacity;element.style.transform=`translateX(${positionX}px) translateY(${positionY}px)`;if(opacity<1||positionX<0||positionY<0){requestAnimationFrame(animate)}else{element.classList.add("landed")}};animate()}},delay)};statNumbers.forEach((stat,index)=>{animateWithDelay(stat,index*1500)});if(availabilityElement){animateWithDelay(availabilityElement,statNumbers.length*1500)}observer.unobserve(entry.target)}})},{threshold:.5});observer.observe(stats)}const backToTopButton=document.getElementById("back-to-top");if(backToTopButton){window.addEventListener("scroll",()=>{if(window.pageYOffset>300){backToTopButton.classList.remove("hidden");backToTopButton.classList.remove("invisible");backToTopButton.classList.add("opacity-100")}else{backToTopButton.classList.add("invisible");backToTopButton.classList.remove("opacity-100");backToTopButton.classList.add("opacity-0")}});backToTopButton.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})})}document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener("click",function(e){e.preventDefault();const target=document.querySelector(this.getAttribute("href"));if(target){target.scrollIntoView({behavior:"smooth"})}})})}); 